mapper.update:
UPDATE TB_BOARDFIELD SET BOARD_FIELD = #{FIELD_DATA}

, FIELD_USE = #{FIELD_USE} , FIELD_DEL = #{FIELD_DEL}

 WHERE FIELD_IDX=#{FIELD_KEY};

mapper.select:
  SELECT BL.BOARD_IDX, BOARD_NAME, BOARD_DATE,
(SELECT COUNT(*) FROM TB_BOARDFIELD WHERE  BOARD_IDX = BL.BOARD_IDX AND FIELD_DEL='N') CNT 
FROM TB_BOARDLIST BL;
